import React, { Fragment, useState } from "react";
import { Link } from "react-router-dom";

import { Button } from "@material-ui/core";

const Login = () => {
  const [formData, setFormData] = useState({
      email: '',
      password: ''
  });

  const { email, password } = formData;

  const onChange = e => setFormData({ ...formData, [e.target.name]: e.target.value });

  const onSubmit = e => {
    e.preventDefault();
    console.log('SUCCESS');
  };

  return (
    <Fragment>
      <h1 className="large text-primary">Sign In</h1>
      <p className="lead"><i className="fas fa-user"></i> Sign In to Your Account</p>
      <form className="form" onSubmit={e => onSubmit(e)}>
        <div className="form-group">
          <input type="email" placeholder="Email Address" name="email" value={email} onChange={e => onChange(e)} required />
        </div>
        <div className="form-group">
          <input type="password" placeholder="Password" name="password" value={password} onChange={e => onChange(e)} minLength="6" required/>
        </div>
        <input type="submit" className="btn btn-primary" value="Login" />
        <Button varient="contained" color="primary">Login</Button>
      </form>
      <p className="my-1">Don't have an account? <Link to="/register">Sign Up</Link></p>
    </Fragment>
  );
};

export default Login;
